<script>
import IndexStrategyForm from "./IndexStrategyForm.vue";
import { post } from "../../../network/requests";
import { getUrl } from "../../../network/url";
import { authHeader, getNumber } from "../../../services/helper";
import { computed } from "vue";

export default {
  components: {
    IndexStrategyForm,
  },
  data() {
    return {
      activeTab: 1,
      tabs: {
        tab1: true,
        tab2: false,
        tab3: false,
      },
      weighting: {
        tab1: "100%",
        tab2: "",
        tab3: "",
      },
      errors: {
        1: [],
        2: [],
        3: [],
        global: [],
      },
      beginningBalance: "",
      taxRate: "0",
      vehicleFee: "1",
      vehicleType: "Taxable",
      totalWeighting: 100,
      submitBtn: false,
    };
  },
  provide() {
    // use function syntax so that we can access `this`
    return {
      errors: computed(() => this.errors),
    };
  },
  methods: {
    testFunction: function () {
      console.log(document.getElementById("weighting_index1").value);
      console.log(document.getElementById("weighting_index2").value);
      console.log(document.getElementById("weighting_index3").value);
      console.log(this.weighting);
    },
    setActiveTab: function (tab) {
      if (tab === 1) {
        this.tabs.tab1 = true;
        this.activeTab = tab;
        if (!this.tabs.tab2 && !this.tabs.tab3) {
          this.weighting.tab1 = "100%";
          this.$refs.weighting_index1.value = "100%";
        }
        if (!this.tabs.tab2) {
          this.weighting.tab2 = "";
          this.$refs.weighting_index2.value = "";
        }
        if (!this.tabs.tab3) {
          this.weighting.tab3 = "";
          this.$refs.weighting_index3.value = "";
        }
      }

      if (tab === 2) {
        this.tabs.tab2 = true;
        this.activeTab = tab;
        if (!this.weighting.tab2) {
          this.weighting.tab1 = "50%";
          this.$refs.weighting_index1.value = "50%";

          this.weighting.tab2 = "50%";
          this.$refs.weighting_index2.value = "50%";
        }

        if (!this.tabs.tab3) {
          this.weighting.tab3 = "";
          this.$refs.weighting_index3.value = "";
        }
      }

      if (tab === 3) {
        this.tabs.tab3 = true;
        this.tabs.tab2 = true;
        this.activeTab = tab;

        if (!this.weighting.tab3) {
          this.weighting.tab1 = "33.34%";
          this.weighting.tab2 = "33.33%";
          this.weighting.tab3 = "33.33%";

          this.$refs.weighting_index1.value = "33.34%";
          this.$refs.weighting_index2.value = "33.33%";
          this.$refs.weighting_index3.value = "33.33%";
        }
      }
    },
    handleCheckBox1: function (event) {
      event.stopPropagation();
      event.preventDefault();
    },
    handleCheckBox2: function (event) {
      event.stopPropagation();
      if (this.activeTab === 2) {
        this.tabs.tab3 = false;
        this.tabs.tab2 = false;
        this.activeTab = 1;
        this.weighting.tab1 = "100%";
        this.weighting.tab2 = "";
        this.weighting.tab3 = "";

        this.$refs.weighting_index1.value = "100%";
        this.$refs.weighting_index2.value = "";
        this.$refs.weighting_index3.value = "";

        return false;
      }

      if (this.tabs.tab2 && this.tabs.tab3) {
        this.tabs.tab2 = false;
        this.tabs.tab3 = false;
      }

      if (event.target.checked) {
        this.weighting.tab1 = "50%";
        this.weighting.tab2 = "50%";
        this.weighting.tab3 = "";

        this.$refs.weighting_index1.value = "50%";
        this.$refs.weighting_index2.value = "50%";
        this.$refs.weighting_index3.value = "";
      } else {
        this.weighting.tab1 = "100%";
        this.weighting.tab2 = "";
        this.weighting.tab3 = "";

        this.$refs.weighting_index1.value = "100%";
        this.$refs.weighting_index2.value = "";
        this.$refs.weighting_index3.value = "";
      }
    },
    handleCheckBox3: function (event) {
      event.stopPropagation();
      if (this.activeTab === 3) {
        this.tabs.tab3 = false;
        this.activeTab = 2;
      }

      if (!this.tabs.tab2 || this.activeTab === 3) {
        event.preventDefault();
      }

      if (this.tabs.tab2 && event.target.checked) {
        this.weighting.tab1 = "33.34%";
        this.weighting.tab2 = "33.33%";
        this.weighting.tab3 = "33.33%";

        this.$refs.weighting_index1.value = "33.34%";
        this.$refs.weighting_index2.value = "33.33%";
        this.$refs.weighting_index3.value = "33.33%";
      } else {
        if (this.tabs.tab2) {
          this.weighting.tab1 = "50%";
          this.weighting.tab2 = "50%";

          this.$refs.weighting_index1.value = "50%";
          this.$refs.weighting_index2.value = "50%";
        } else {
          this.weighting.tab1 = "100%";
          this.weighting.tab2 = "";

          this.$refs.weighting_index1.value = "100%";
          this.$refs.weighting_index2.value = "";
        }

        this.weighting.tab3 = "";
        this.$refs.weighting_index3.value = "";
      }
    },
    getActiveTabs: function () {
      return [this.tabs.tab1, this.tabs.tab2, this.tabs.tab3];
    },
    getIndexWeighting: function () {
      let w1 =
        Number(
          (Number(this.weighting.tab1.replace("%", "")) / 100).toFixed(2)
        ) || 0;
      let w2 =
        Number(
          (Number(this.weighting.tab2.replace("%", "")) / 100).toFixed(2)
        ) || 0;
      let w3 =
        Number(
          (Number(this.weighting.tab3.replace("%", "")) / 100).toFixed(2)
        ) || 0;
      if (w1 + w2 + w3 === 0.99) {
        w1 = w1 + 0.01;
      }
      return [w1, w2, w3];
    },
    getVehicleData: function () {
      let v_type = "taxable";
      if (this.vehicleType === "Pre-Tax") {
        v_type = "pre-tax";
      }

      let obj = {
        beginning_balance: getNumber(this.beginningBalance),
        vehicle_type: v_type,
        tax_rate: Number(this.taxRate),
        vehicle_fee: Number(this.vehicleFee),
      };

      return obj;
    },
    getIndexStrategiesData: function () {
      let arr = [];
      let activeTabs = this.getActiveTabs();

      let totalActiveTab = activeTabs.filter((i) => i).length;
      let weightings = this.getIndexWeighting();

      for (var i = 1; i < 4; i++) {
        let start_year = document.getElementById(`index_1_startYear`).value;
        let end_year = document.getElementById(`index_1_endYear`).value;
        let segment = document.getElementById(`index_1_segment`).value;
        let pmsy = document.getElementById(`index_1_pmfStartYear`).value;
        let fcbsy = document.getElementById(`index_1_fcStartYear`).value;
        let cap_rate = document.getElementById(`index_1_capRate`).value;
        let margin = document.getElementById(`index_1_margin`).value;
        let par_rate = document.getElementById(`index_1_parRate`).value;
        let floor = document.getElementById(`index_1_floor`).value;
        let fcb = document.getElementById(`index_1_flatCreditBonus`).value;
        let pm = document.getElementById(`index_1_PerformanceMultiplier`).value;
        let fees = document.getElementById(`index_1_StrategyFee`).value;

        if (activeTabs[i - 1]) {
          let obj = {
            allocation: weightings[i - 1],
            start_year: start_year ? Number(start_year) : "",
            end_year: end_year ? Number(end_year) : "",
            segment_duration: segment ? Number(segment) : "",
            performance_multiplier_start_year: pmsy ? Number(pmsy) : "",
            flat_credit_bonus_start_year: fcbsy ? Number(fcbsy) : "",
            index: document.getElementById(`index_1_indexStrategy`).value,
            cap_rate: cap_rate ? Number(cap_rate) : "",
            margin: margin ? Number(margin) : "",
            par_rate: par_rate ? Number(par_rate) : "",
            floor: floor ? Number(floor) : "",
            flat_credit_bonus: fcb ? Number(fcb) : "",
            performance_multiplier: pm ? Number(pm) : "",
            fee: fees ? Number(fees) : "",
          };
          arr.push(obj);
        }
      }
      return arr;
    },
    getStrategyObject: function (index) {
      let strategy = this.getIndexStrategiesData();
      return {
        allocation: strategy[index].allocation,
        index: strategy[index].index,
        segment_duration: strategy[index].segment_duration,
        cap_rate: strategy[index].cap_rate,
        margin: strategy[index].margin,
        par_rate: strategy[index].par_rate,
        floor: strategy[index].floor,
        performance_multiplier: strategy[index].performance_multiplier,
        performance_multiplier_start_year:
          strategy[index].performance_multiplier_start_year,
        flat_credit_bonus: strategy[index].flat_credit_bonus,
        flat_credit_bonus_start_year:
          strategy[index].flat_credit_bonus_start_year,
        fee: strategy[index].fee,
      };
    },
    clearError: function (key, tab = false) {
      if (this.beginningBalance && this.taxRate !== "") {
        this.submitBtn = true;
      } else {
        this.submitBtn = false;
      }

      if (tab) {
        this.errors[tab][key] = "";
      } else {
        this.errors.global[key] = "";
      }
    },
    validateStrategyForm: function (tab) {
      let valid = true;
      let strategy = this.getStrategyObject(tab - 1);
      let start_year = document.getElementById(`index_1_startYear`).value;
      let end_year = document.getElementById(`index_1_endYear`).value;
      let maxYear = Number(end_year) - Number(start_year);

      if (strategy.cap_rate !== "" && strategy.cap_rate < 1) {
        this.errors[tab].cap_rate = "At least 1% value is required.";
        valid = false;
      }

      if (strategy.par_rate === "") {
        this.errors[tab].par_rate = "This field is required.";
        valid = false;
      }

      if (strategy.par_rate !== "" && strategy.par_rate < 1) {
        this.errors[tab].par_rate = "At least 1% value is required.";
        valid = false;
      }

      if (strategy.floor === "") {
        this.errors[tab].floor = "This field is required.";
        valid = false;
      }

      if (strategy.flat_credit_bonus !== "" && strategy.flat_credit_bonus < 0) {
        this.errors[tab].flat_credit_bonus = "At least 0 value is required.";
        valid = false;
      }

      if (strategy.fee !== "" && strategy.fee < 0) {
        this.errors[tab].fee = "At least 0 value is required.";
        valid = false;
      }

      if (strategy.performance_multiplier_start_year > maxYear) {
        this.errors[tab].pm_start_year =
          "Maximum start year must be less than " + maxYear;
        valid = false;
      }

      if (strategy.flat_credit_bonus_start_year > maxYear) {
        this.errors[tab].fc_start_year =
          "Maximum start year must be less than " + maxYear;
        valid = false;
      }

      return valid;
    },
    validateForm: function () {
      let valid = true;

      if (!this.beginningBalance) {
        valid = false;
        this.errors.global["beginning_balance"] = "This field is required.";
      }

      if (this.beginningBalance && Number(this.beginningBalance) < 1) {
        valid = false;
        this.errors.global["beginning_balance"] = "At least $1 required.";
      }

      if (!this.taxRate) {
        valid = false;
        this.errors.global["tax_rate"] = "This field is required.";
      }

      if (this.vehicleFee && Number(this.vehicleFee) < 0) {
        valid = false;
        this.errors.global["vehicle_fee"] = "Minimum value should be 0";
      }

      if (!this.validateStrategyForm(1)) {
        valid = false;
      }

      return valid;
    },
    submitHandler: function () {
      if (!this.validateForm()) {
        return false;
      }

      let vehicle = this.getVehicleData();
      let strategy = this.getIndexStrategiesData();

      let formData = {
        beginning_balance: vehicle.beginning_balance,
        start_year: strategy[0].start_year,
        end_year: strategy[0].end_year,
        index_vehicle: {
          type: vehicle.vehicle_type,
          tax_rate: vehicle.tax_rate,
          fee: vehicle.vehicle_fee,
        },
        strategies: [this.getStrategyObject(0)],
      };

      if (this.tabs.tab2) {
        formData.strategies[1] = this.getStrategyObject(1);
      }

      if (this.tabs.tab3) {
        formData.strategies[2] = this.getStrategyObject(2);
      }

      this.$store.dispatch("loader", true);
      localStorage.removeItem("isc_calculate");
      localStorage.setItem("isc_calculate_inputs", JSON.stringify(formData));

      post(getUrl("isc_calculate"), formData, authHeader())
        .then((response) => {
          let data = JSON.parse(
            response.data.replaceAll("NaN", "0").replaceAll("Infinity", "0")
          );
          this.$store.dispatch("loader", false);
          localStorage.setItem(
            "isc_calculate",
            JSON.stringify(this.mapData(data))
          );
          this.$router.push(`/index-strategy-calculator-run`);
        })
        .catch((error) => {
          console.log(error);
          if (
            error.code === "ERR_BAD_RESPONSE" ||
            error.code === "ERR_NETWORK"
          ) {
            this.$toast.error(error.message);
          } else {
            this.$toast.error("Something went wrong!");
          }
          this.$store.dispatch("loader", false);
        });
    },
    mapData: function (data) {
      let years = Object.values(data.index_results.year);
      let index_rate_of_returns = data.index_results.rate_of_return;
      let index_net_balances = [];

      if (this.vehicleType === "Taxable") {
        index_net_balances = data.index_results.eoy_net_balance;
      } else {
        index_net_balances = data.index_results.net_balance;
      }

      let strategy_rate_of_returns = data.strategy_results.strategy_return;
      let strategy_net_balances = data.strategy_results.net_balance;

      let index_results = [];
      let strategy_results = [];
      years.forEach((element, index) => {
        index_results.push({
          year: element,
          ror: index_rate_of_returns[index],
          net_balance: index_net_balances[index],
        });

        strategy_results.push({
          year: element,
          ror: strategy_rate_of_returns[index],
          net_balance: strategy_net_balances[index],
        });
      });

      return {
        index_results: index_results,
        strategy_results: strategy_results,
        index_summary: data.index_summary,
        strategy_summary: data.strategy_summary,
      };
    },
  },
  computed: {
    vehicleTypes() {
      return [
        { name: "Taxable", value: "taxable" },
        { name: "Pre-Tax", value: "pre-tax" },
      ];
    },
  },
  mounted() {
    // Select Dropdown Start
    let selectBtn = document.querySelectorAll(".select-btn");
    selectBtn.forEach((showHide) => {
      showHide.addEventListener("click", () =>
        showHide.closest(".select-menu").classList.toggle("active")
      );
      var allOptions = showHide
        .closest(".select-menu")
        .querySelector(".options")
        .querySelectorAll(".option");
      allOptions.forEach((option) => {
        option.addEventListener("click", (e) => {
          e.stopPropagation();
          option.parentElement.parentElement.classList.remove("active");
        });
      });
    });

    let dropdowns = document.querySelectorAll(".select-menu");
    dropdowns.forEach((element) => {
      element.addEventListener("click", (e) => {
        dropdowns.forEach((item) => {
          if (item.className.includes("active")) {
            item.classList.remove("active");
          }
        });
        e.target.closest(".select-menu").classList.add("active");
      });
    });

    // Close when click outside
    window.onclick = function (event) {
      if (!event.target.matches(".select-menu")) {
        var sharedowns = document.getElementsByClassName("select-menu");
        var i;
        for (i = 0; i < sharedowns.length; i++) {
          var openSelectdropdown = sharedowns[i];
          if (openSelectdropdown.classList.contains("active")) {
            openSelectdropdown.classList.remove("active");
          }
        }
      }
    };

    var allSelectMenus = document.querySelectorAll(".select-menu");
    allSelectMenus.forEach((eachSelectMenus) => {
      eachSelectMenus.addEventListener("click", function (event) {
        event.stopPropagation();
      });
    });

    // input validation for min and max value
    const inputs = document.querySelectorAll(".onlyPositiveNum");
    inputs.forEach((element) =>
      element.addEventListener("input", function (e) {
        let len = e.target.value.length;
        let current = e.target.value;
        let min = Number(e.target.getAttribute("min"));
        let max = Number(e.target.getAttribute("max"));
        if (
          Number(current) < min ||
          Number(current) > max ||
          isNaN(Number(current))
        ) {
          let actualValue = current.slice(0, len - 1);
          e.target.value = actualValue;
          return false;
        }
      })
    );

    // input validation for min and max value with putting comma
    const inputs2 = document.querySelectorAll(".handleLimit");
    inputs2.forEach((element) =>
      element.addEventListener("input", function (e) {
        let current = getNumber(e.target.value).toString();
        let min = Number(e.target.getAttribute("min"));
        let max = Number(e.target.getAttribute("max"));
        if (Number(current) < min || Number(current) > max) {
          let actualValue = current.slice(0, current.length - 1);
          e.target.value =
            Number(current) < min ? "" : Number(actualValue).toLocaleString();
          return false;
        } else {
          e.target.value = Number(current).toLocaleString();
        }
      })
    );

    function getTotalWeighting() {
      let w1 =
        Number(
          document.getElementById("weighting_index1").value.replace("%", "")
        ) || 0;
      let w2 =
        Number(
          document.getElementById("weighting_index2").value.replace("%", "")
        ) || 0;
      let w3 =
        Number(
          document.getElementById("weighting_index3").value.replace("%", "")
        ) || 0;
      // return Number((w1 + w2 + w3).toFixed(0));
      return w1 + w2 + w3;
    }

    // input validation for min and max value with putting comma
    const inputs3 = document.querySelectorAll(".allocation-input");
    inputs3.forEach((element) =>
      element.addEventListener("input", function (e) {
        e.target.value = e.target.value.replace("%", "");
        let len = e.target.value.length;
        let current = e.target.value;
        let min = Number(e.target.getAttribute("min"));
        let max = Number(e.target.getAttribute("max"));
        let modelInputId = e.target.getAttribute("modelInputId");
        if (
          Number(current) < min ||
          Number(current) > max ||
          isNaN(Number(current))
        ) {
          let actualValue = current.slice(0, len - 1);
          e.target.value = actualValue;
        }

        document.getElementById(modelInputId).value = e.target.value;

        if (getTotalWeighting() === 100) {
          document
            .getElementById("allocation-all-div")
            .classList.remove("error");
        } else {
          document.getElementById("allocation-all-div").classList.add("error");
        }

        return false;
      })
    );

    inputs3.forEach((element) =>
      element.addEventListener("focus", function (e) {
        e.target.value = e.target.value.replace("%", "");
      })
    );

    inputs3.forEach((element) =>
      element.addEventListener("blur", function (e) {
        e.target.value = e.target.value.replaceAll("%", "") + "%";
      })
    );

    inputs3.forEach((element) =>
      element.addEventListener("focusout", function (e) {
        e.target.value = e.target.value.replaceAll("%", "") + "%";
      })
    );
  },
};
</script>
<style>
.error {
  color: red !important;
}
</style>
